import React, { Component } from "react";
import InputForm from "./../InputForm/InputForm";
// import { render } from "@testing-library/react";

class Form extends Component {
  state = {
    good: 0,
    neutral: 0,
    bad: 0,
  };

  handleSabmit = (e) => {
    e.preventDefault();
    this.setState({ ...this.state });
  };
  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  render() {
    // console.log(this.state.bad);
    const { good, neutral, bad } = this.state;
    return (
      <form onSubmit={this.handleSabmit}>
        <InputForm handleChange={this.handleChange} value={good} />
        <InputForm handleChange={this.handleChange} value={neutral} />
        <InputForm handleChange={this.handleChange} value={bad} />
      </form>
    );
  }
}

export default Form;
