import React, { Component } from "react";
import { InputForm } from "./InputForm/InputForm";
import { BtnFormSubmit } from "./Button/BtnFormSubmit";
import { v4 as uuidv4 } from "uuid";


class Phonebook extends Component {
  state = {
    contacts: [],
    name: "",
  };
  handleSubmit = (e) => {
    e.preventDefault();
    const contact = this.state.name;
    const contacts = this.state.contacts;
    if (contact) {
      const newContact = { id: uuidv4(), contact };
    //   this.setState({ ...contacts }.push(newContact));
      this.props.getUserInfo(newContact);
    }
  };
  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };
  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <InputForm name={this.state.name} handleChange={this.handleChange} />
        <BtnFormSubmit />
      </form>
    );
  }
}
export default Phonebook;
